import { createRouter, createWebHistory } from 'vue-router'
import  Layout  from '../layout/Layout.vue'
import register from "@/views/Register";
const routes = [
  {
    path: '/',
    name: 'Layout ',
    redirect: "/home",
    component: Layout,

    children: [
      {
        path: 'home',
        name: 'Home ',
        component: () => import('@/views/HomeView.vue')
      }
    ]
